//阿里OSS
define(['jquery'], function ($) {
    var instance = {};
    instance.upload = function (options) {
        opt=$.extend({
            el:'',
            //获取签名的后台地址
            serverUrl:hdjs.ossSign,
            //在OSS中储存object的目录
            dir:'',
            //文件名类型,random_name:随机字符 local_name:原文件名
            name_type:'random_name',
            //上传成功回调函数
            success:function(file,info){},
            //发生错误时的回调函数
            error:function(error_message){},
            //选择文件后执行的回调函数,可用于显示进度条dom
            filesAdded:function(file){},
            //进度回调,可用于生成进度条
            progress:function(file){},
            //图片类型
            image:"jpg,gif,png,bmp",
            //文件类型
            file:"zip,rar,mp4",
            //文件上传大小
            max_file_size: '200mb',
            //不允许选择重复文件
            prevent_duplicates:true
        },options);
        accessid = ''
        accesskey = ''
        host = ''
        policyBase64 = ''
        signature = ''
        callbackbody = ''
        filename = ''
        key = ''
        expire = 0
        //OSS中文件名
        g_object_name = ''
        //文件名类型  random_name: 随机字符   local_name: 原文件名
        g_object_name_type = opt.name_type
        now = timestamp = Date.parse(new Date()) / 1000;

        function send_request() {
            var xmlhttp = null;
            if (window.XMLHttpRequest) {
                xmlhttp = new XMLHttpRequest();
            }
            else if (window.ActiveXObject) {
                xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");
            }

            if (xmlhttp != null) {
                serverUrl = opt.serverUrl+"&dir="+opt.dir,
                xmlhttp.open("GET", serverUrl, false);
                xmlhttp.send(null);
                return xmlhttp.responseText
            }
            else {
                alert("Your browser does not support XMLHTTP.");
            }
        };
        function get_signature() {
            //可以判断当前expire是否超过了当前时间,如果超过了当前时间,就重新取一下.3s 做为缓冲
            now = timestamp = Date.parse(new Date()) / 1000;
            if (expire < now + 3) {
                body = send_request()
                var obj = eval("(" + body + ")");
                host = obj['host']
                policyBase64 = obj['policy']
                accessid = obj['accessid']
                signature = obj['signature']
                expire = parseInt(obj['expire'])
                callbackbody = obj['callback']
                key = obj['dir']?obj['dir']:'';
                return true;
            }
            return false;
        };

        function random_string(len) {
            len = len || 32;
            var chars = 'ABCDEFGHJKMNPQRSTWXYZabcdefhijkmnprstwxyz2345678';
            var maxPos = chars.length;
            var pwd = '';
            for (i = 0; i < len; i++) {
                pwd += chars.charAt(Math.floor(Math.random() * maxPos));
            }
            return pwd;
        }

        function get_suffix(filename) {
            pos = filename.lastIndexOf('.')
            suffix = ''
            if (pos != -1) {
                suffix = filename.substring(pos)
            }
            return suffix;
        }

        function calculate_object_name(filename) {
            if (g_object_name_type == 'local_name') {
                g_object_name += "${filename}"
            }
            else if (g_object_name_type == 'random_name') {
                suffix = get_suffix(filename)
                g_object_name = key + random_string(10) + suffix
            }
            return ''
        }

        function get_uploaded_object_name(filename) {
            //直接返回随机文件名
            if (g_object_name_type == 'local_name') {
                tmp_name = g_object_name
                tmp_name = tmp_name.replace("${filename}", filename);
                return tmp_name
            }
            else if (g_object_name_type == 'random_name') {
                return g_object_name
            }
        }

        function set_upload_param(up, filename, ret) {
            if (ret == false) {
                ret = get_signature()
            }
            g_object_name = key;
            if (filename != '') {
                suffix = get_suffix(filename)
                calculate_object_name(filename)
            }
            new_multipart_params = {
                'key': g_object_name,
                'policy': policyBase64,
                'OSSAccessKeyId': accessid,
                'success_action_status': '200', //让服务端返回200,不然，默认会返回204
                'callback': callbackbody,
                'signature': signature,
            };

            up.setOption({
                'url': host,
                'multipart_params': new_multipart_params
            });

            up.start();
        }

        var uploader = new plupload.Uploader({
            runtimes: 'html5,flash,silverlight,html4',
            browse_button: opt.el,
            multi_selection: false,
            // container: document.getElementById('container'),
            flash_swf_url: hdjs.base+'/component/oss/plupload-2.1.2/js/Moxie.swf',
            silverlight_xap_url: hdjs.base+'/component/oss/plupload-2.1.2/js/Moxie.xap',
            url: 'http://oss.aliyuncs.com',
            filters: {
                mime_types: [ //只允许上传图片和zip,rar文件
                    {title: "Image files", extensions: opt.image},
                    {title: "Zip files", extensions: opt.file}
                ],
                max_file_size: opt.max_file_size, //最大只能上传10mb的文件
                prevent_duplicates: opt.prevent_duplicates //不允许选取重复文件
            },
            init: {
                PostInit: function () {
                    // document.getElementById('ossfile').innerHTML = '';
                    // document.getElementById('postfiles').onclick = function () {
                    //     set_upload_param(uploader, '', false);
                    //     return false;
                    // };
                },
                //选中文件后执行的回调函数
                FilesAdded: function (up, files) {
                    opt.filesAdded(files);
                    // plupload.each(files, function (file) {
                    //     document.getElementById('ossfile').innerHTML += '<div id="' + file.id + '">' + file.name + ' (' + plupload.formatSize(file.size) + ')<b></b>'
                    //         + '<div class="progress"><div class="progress-bar" style="width: 0%"></div></div>'
                    //         + '</div>';
                    // });

                    //直接开始上传
                    set_upload_param(uploader, '', false);
                },
                //上传前回调方法
                BeforeUpload: function (up, file) {
                    set_upload_param(up, file.name, true);
                },
                //上传进度回调函数
                UploadProgress: function (up, file) {
                    opt.progress(file);
                    // var d = document.getElementById(file.id);
                    // d.getElementsByTagName('b')[0].innerHTML = '<span>' + file.percent + "%</span>";
                    //          var prog = d.getElementsByTagName('div')[0];
                    // var progBar = prog.getElementsByTagName('div')[0]
                    // progBar.style.width= 2*file.percent+'px';
                    // progBar.setAttribute('aria-valuenow', file.percent);
                },
                //上传完毕
                FileUploaded: function (up, file, info) {
                    if (info.status == 200) {
                        opt.success(file,info);
                        // document.getElementById(file.id).getElementsByTagName('b')[0].innerHTML = 'upload to oss success, object name:' + host + '/' + get_uploaded_object_name(file.name);
                    }
                    else {
                        opt.error(info.response);
                        // document.getElementById(file.id).getElementsByTagName('b')[0].innerHTML = info.response;
                    }
                },

                Error: function (up, err) {
                    if (err.code == -600) {
                        opt.error('选择的文件太大了,可以根据应用情况');
                        // document.getElementById('console').appendChild(document.createTextNode("\n选择的文件太大了,可以根据应用情况，在upload.js 设置一下上传的最大大小"));
                    }
                    else if (err.code == -601) {
                        opt.error('选择的文件后缀不对,可以根据应用情况');
                        // document.getElementById('console').appendChild(document.createTextNode("\n选择的文件后缀不对,可以根据应用情况，在upload.js进行设置可允许的上传文件类型"));
                    }
                    else if (err.code == -602) {
                        opt.error('这个文件已经上传过一遍了');
                        // document.getElementById('console').appendChild(document.createTextNode("\n这个文件已经上传过一遍了"));
                    }
                    else {
                        opt.error(err.response);
                        // document.getElementById('console').appendChild(document.createTextNode("\nError xml:" + err.response));
                    }
                }
            }
        });

        uploader.init();
    }
    return instance;
});